import './MyCurve.css';
import { withStyles } from '@mui/material/styles';
import React, {Component} from 'react';
import * as d3 from "d3";
import Finance from 'financejs';
import Tabs from '@mui/material/Tabs';
import Typography from '@mui/material/Typography';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';

class MyCurve extends Component {
  constructor() {
    super();
  }

  componentDidMount() {
    this.drawChart();
  }

  drawChart() {
    //compound curve 1
    const data = [12, 5, 6, 6, 9, 10];
    var w = 700, h = 300;
    const svg = d3.select("#MyCurve")
                .append("svg")
                .attr("width", w)
                .attr("height", h);

    svg.selectAll("rect")
        .data(data)
        .enter()
        .append("rect")
        .attr("x", (d, i) => i * 70)
        .attr("y", (d, i) => h - 10 * d)
        .attr("width", 65)
        .attr("height", (d, i) => d * 10)
        .attr("fill", "green");
  }

  render() {
    return (
        <Box
          sx={{ flexGrow: 1, bgcolor: 'background.paper', display: 'flex'}}
        >
        <div className="container">
          <div id="MyCurveDataEntry">
            <table className="mycurve-data">
            <tbody>
              <tr>
                <td>
                  <label>Starting Amount</label>
                  <input id="" type="number" id="starting_amt" />
                </td>
                <td>
                  <label>Retirement Year (from Start)</label>
                  <input type="number" id="retirement" />
                </td>
              </tr>
              <tr>
                <td>
                  <label>Annual Contributions</label>
                  <input type="number" id="contributions" />
                </td>
                <td>
                  <label>Withdrawals Per Year (%)</label>
                  <input type="number" id="withdrawal_pc" />
                </td>
              </tr>
              <tr>
                <td>
                  <label>Number of Years Invested</label>
                  <input type="number" name="years" />
                </td>
                <td>
                </td>
              </tr>
              </tbody>
            </table>
            <Button className="mycurve-btn"
                    variant="contained"
                    size="large"
            >
              See My Curve
            </Button>
          </div>
          <div id="MyCurve"></div>
        </div>
      </Box>
    )
  }

}

export default MyCurve;
