import React, { Component } from 'react';
import Finance from 'financejs';
import { ContactSupportOutlined } from '@material-ui/icons';

class GoalSeek extends React.Component {
  constructor() {
    //
    super();
  }
  seek(initialValue, accountArr, targetValue) {
    const finance = new Finance();
    const n = accountArr.length;  // number of periods
    let rate = .1;  // initial guess for the rate of return
    //populate accountValuesArr
    let accountValuesArr = [];
    for (let i = 0; i < n; i++) {
      accountValuesArr.push(accountArr[i].balEOY);
    }
    // calculate the net present value of the investments at the initial guess for the rate of return
    let pv = initialValue;
    for (let i = 0; i < n; i++) {
      pv += accountValuesArr[i] / Math.pow(1 + rate, i + 1);
    }

    // iterate until the net present value is close enough to the target value
    const tolerance = 1e-2;  // tolerance for the error between the net present value and the target value
    while (Math.abs(pv - targetValue) > tolerance) {
      // adjust the rate of return based on the error between the net present value and the target value
      var diff = Math.abs(pv - targetValue);
      rate += (targetValue - pv) / pv;

      // recalculate the net present value at the new rate of return
      pv = initialValue; //reset pv to initial value
      for (let i = 0; i < n; i++) {
        pv += accountValuesArr[i] / Math.pow(1 + rate, i + 1);
        console.log(pv, rate, diff);
      }
      return rate;
    }
  }
}

export default GoalSeek;
