import './SimpleInterestCalc.css';
import RATE from './helpers/rate.js';
import Finance from 'financejs';
import ToolTip from './snippets/tooltip.js';
import React from 'react';
import { Component } from 'react';
import { useState } from 'react';
import { withStyles } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';

const finance = new Finance();

class SimpleInterestCalc extends Component {
      constructor() {
        super();
        this.def = {
              presVal: 0,
              futureVal: 1000,
              annualContributions: 1500,
              numYears: 30
            };
        this.RATE = RATE;
        const [value, setValue] = useState("");
      }

      componentDidMount() {
      }

      handleChange(e) {
        setValue(e.target.value);
      };

      handleSubmit(e) {
        e.preventDefault();
        this.calculate(
            this.def.numYears,
            this.def.annualContributions,
            this.def.presVal,
            this.def.futureVal, 0, 10);
      };

      calculate(per, contrib, pres, fut, type, guess) {
        console.log("Calculate Function: ", per, contrib, pres, fut, type, guess);
        // * @usage RATE($periods, $payment, $present, $future, $type, $guess)
        var rate = this.RATE(per, contrib, pres, fut, type, guess);
        console.log(rate);
      }

      render() {
        return (
            <Box
              sx={{ flexGrow: 1, bgcolor: 'background.paper', display: 'flex'}}
            >
            <div className="container">
              <div id="CalcDataEntry">
                <table className="mycurve-data">
                <tbody>
                  <tr>
                    <td>
                      <label>Present Value <ToolTip /></label>
                      <input  type="number"
                              id="principal"
                              placeholder={this.def.presVal}
                              onChange={this.handleChange} />
                    </td>
                    <td>
                      <label>Future Value <ToolTip /></label>
                      <input  type="number"
                              id="principal"
                              placeholder={this.def.futureVal}
                              onChange={this.handleChange} />
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <label>Annual Contributions <ToolTip /></label>
                      <input  type="number"
                              id="annual_contributions"
                              placeholder={this.def.annualContributions}
                              onChange={this.handleChange} />
                    </td>
                    <td>
                      <label>Number of Years <ToolTip /></label>
                      <input  type="number"
                              id="num_of_years"
                              placeholder={this.def.numYears}
                              onChange={this.handleChange} />
                    </td>
                  </tr>
                  </tbody>
                </table>
                <Button className="calc-btn"
                        variant="contained"
                        size="large"
                        onClick={this.handleSubmit}
                >
                  Calculate
                </Button>
              </div>
            </div>
          </Box>
        )
      }

  }


export default SimpleInterestCalc;
