import React, { Component } from 'react';
import Finance from 'financejs';

class GoalSeek extends React.Component {
  constructor() {
    //
    super();
    this.finance = new Finance();
  }
  seek(initialValue, dataTable, targetValue) {
    const n = dataTable.length;  // number of periods
    let rate = .1;  // initial guess for the rate of return

    // calculate the net present value of the investments at the initial guess for the rate of return
    let npv = this.calcNpv(rate, initialValue, dataTable, n);
    console.log("initialValue:", initialValue, " npv:", npv, " targetValue:", targetValue);

    // iterate until the net present value is close enough to the target value, or until the maximum number of iterations is reached
    const tolerance = 1e-2;  // tolerance for the error between the net present value and the target value
    const maxIterations = 1e+5;  // maximum number of iterations
    let iterations = 0;  // current number of iterations
    while (Math.abs(npv - targetValue) > tolerance && iterations < maxIterations) {
      // adjust the rate of return based on the error between the net present value and the target value
      rate -= (targetValue - npv) / npv;

      // recalculate the net present value at the new rate of return
      npv = this.calcNpv(rate, dataTable, n);
      console.log(npv, Math.abs(npv - targetValue), rate);
      iterations++;
    }

    if (iterations === maxIterations) {
      throw new Error('Could not find a solution within the maximum number of iterations');
    }
    return rate;

  }

  calcNpv(rate, initialValue, dataTable, n) {
    let npv = initialValue;
    for (var i = 0; i < n; i++) {
      npv += this.finance.PV(rate, dataTable[i].annPay - dataTable[i].mngFee, 1);
    }
    return npv;
  }
}

export default GoalSeek;
