import './MyCurve.css';
import { withStyles } from '@mui/material/styles';
import React, {Component} from 'react';
import * as d3 from "d3";
import Finance from 'financejs';
import Tabs from '@mui/material/Tabs';
import Typography from '@mui/material/Typography';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';

class MyCurve extends Component {
  constructor() {
    super();
    this.def = {
          principal: 0,
          contrib: 1500,
          yrs: 30,
          w_rate: 0.04
        };
  }

  componentDidMount() {
    this.drawChart(this.def.principal, this.def.contrib, this.def.yrs, this.def.w_rate);
  }

  drawChart(principal, contrib, yrs, w_rate) {
    //compound curve 1
    //create a dataset
    let data = [];
    for (var n = 0; n < yrs; n++) {

    }

    var w = 700, h = 300;
    const svg = d3.select("#MyCurve")
                .append("svg")
                .attr("width", w)
                .attr("height", h);

  /*  const lineGenerator = d3
       .line()
       .x((d) => xScale(xAccessor(d)))
       .y((d) => yScale(yAccessor(d)))
       .curve(d3.curveBasis);

    svg.selectAll("rect")
        .data(data)
        .enter()
        .append("path")
        .attr("x", (d, i) => i * 70)
        .attr("y", (d, i) => h - 10 * d)
        .attr("width", 65)
        .attr("height", (d, i) => d * 10)
        .attr("fill", "green");
        */
  }

  changed = (event) => {
    var p = (document.getElementById('starting_amt').value == null)   ? this.def.principal  : document.getElementById('starting_amt').value;
    var c = (document.getElementById('contributions').value == null)  ? this.def.contrib    : document.getElementById('contributions').value;
    var y = (document.getElementById('years').value == null)          ? this.def.yrs        : document.getElementById('years').value;
    var w = (document.getElementById('withdrawal_rt').value == null)  ? this.def.w_rate     : document.getElementById('withdrawal_rt').value;

    console.log("CHANGED: " + p, c, y, w);
    this.drawChart(p, c, y, w);
  }

  render() {
    console.log(this.def.principal, this.def.contrib, this.def.yrs, this.def.w_rate);
    console.log(document.getElementById('contributions').value == null)
    return (
        <Box
          sx={{ flexGrow: 1, bgcolor: 'background.paper', display: 'flex'}}
        >
        <div className="container">
          <div id="MyCurveDataEntry">
            <table className="mycurve-data">
            <tbody>
              <tr>
                <td>
                  <label>Starting Amount</label>
                  <input  type="number"
                          id="starting_amt"
                          placeholder={this.def.principal}
                          onChange={(event)=>this.changed(event)}/>
                </td>
                <td>
                  <label>Number of Years Invested</label>
                  <input  type="number"
                          id="years"
                          placeholder={this.def.yrs}
                          onChange={(event)=>this.changed(event)}/>
                </td>
              </tr>
              <tr>
                <td>
                  <label>Annual Contributions</label>
                  <input  type="number"
                          id="contributions"
                          placeholder={this.def.contrib}
                          onChange={(event)=>this.changed(event)}/>
                </td>
                <td>
                  <label>Withdrawal Rate Per Year (%)</label>
                  <input  type="number"
                          id="withdrawal_rt"
                          placeholder={this.def.w_rate}
                          onChange={(event)=>this.changed(event)}/>
                </td>
              </tr>
              </tbody>
            </table>
            {/*<Button className="mycurve-btn"
                    variant="contained"
                    size="large"
            >
              See My Curve
            </Button>*/}
          </div>
          <div id="MyCurve"></div>
        </div>
      </Box>
    )
  }

}

export default MyCurve;
