import './SimpleInterestCalc.css';
import RATE from './helpers/rate.js';
import Finance from 'financejs';
import ToolTip from './snippets/tooltip.js';
import React from 'react';
import { Component } from 'react';
import { withStyles } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';

const finance = new Finance();
var _this;

class SimpleInterestCalc extends Component {
      constructor() {
        super();
        this.def = {  presVal: 0,
                      futureVal: 1000000,
                      annualContributions: 1200,
                      numYears: 40
                    };
        this.RATE = RATE;
        _this = this;
      }

      changed(event) {

        _this.def.presVal = (document.getElementById('presVal').value == "")   ? _this.def.presVal.toLocaleString()  : Number(document.getElementById('presVal').value).toLocaleString();
        _this.def.futureVal = (document.getElementById('futureVal').value == "")   ? _this.def.futureVal.toLocaleString()  : Number(document.getElementById('futureVal').value).toLocaleString();
        _this.def.annualContributions = (document.getElementById('annualContributions').value == "")   ? _this.def.annualContributions.toLocaleString()  : Number(document.getElementById('annualContributions').value).toLocaleString();
        _this.def.numYears = (document.getElementById('numYears').value == "")   ? _this.def.numYears.toLocaleString()  : Number(document.getElementById('numYears').value).toLocaleString();
      }


      calculate() {

        // * @usage RATE($periods, $payment, $present, $future, $type, $guess)
        console.log(_this.def.numYears, _this.def.annualContributions, _this.def.presVal, _this.def.futureVal);
        var per = Number(_this.def.numYears),
            cont = Number(_this.def.annualContributions * -1),
            pv = Number(_this.def.presVal),
            fv = Number(_this.def.futureVal);
        var rate = (_this.RATE(per, cont, pv, fv, 1, 1) * 100).toFixed(2);
        var calcResult = document.getElementById("CalcResult");
        var calcLabel = document.getElementById("CalcLabel");
        calcLabel.innerHTML = "The Interest Rate is"
        calcResult.innerHTML = rate + "%";
      }

      format(id, inputField) {
        console.log(id, inputField);
        /*document.querySelector(".num")[0] = (function(e){
          e.value = +e.value.toLocaleString()
        });*/
      }


      render() {

        return (
            <Box
              sx={{ flexGrow: 1, bgcolor: 'background.paper', display: 'flex'}}
            >
            <div className="container">
              <div id="CalcDataEntry">
                <table className="mycurve-data">
                <tbody>
                  <tr>
                    <td>
                      <label>Present Value <ToolTip /></label>
                      <input  type="number"
                              id="presVal"
                              className="num"
                              onBlur={_this.format(presVal.id, this.def.presVal)}
                              placeholder={this.def.presVal.toLocaleString()}
                              onChange={(event)=>this.changed(event)} />
                    </td>
                    <td>
                      <label>Future Value <ToolTip /></label>
                      <input  type="number"
                              id="futureVal"
                              className="num"
                              onBlur={_this.format(this.def.key, this.def.futureVal)}
                              placeholder={this.def.futureVal.toLocaleString()}
                              onChange={(event)=>this.changed(event)}/>
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <label>Annual Contributions <ToolTip /></label>
                      <input  type="number"
                              id="annualContributions"
                              className="num"
                              onBlur={_this.format(this.def.key, this.def.annualContributions)}
                              placeholder={this.def.annualContributions.toLocaleString()}
                              onChange={(event)=>this.changed(event)} />
                    </td>
                    <td>
                      <label>Number of Years <ToolTip /></label>
                      <input  type="number"
                              id="numYears"
                              className="num"
                              onBlur={_this.format(this.def.key, this.def.numYears)}
                              placeholder={this.def.numYears.toLocaleString()}
                              onChange={(event)=>this.changed(event)} />
                    </td>
                  </tr>
                  </tbody>
                </table>
                <Button className="calc-btn"
                        variant="contained"
                        size="large"
                        onClick={this.calculate}
                >
                  Calculate
                </Button>
              </div>
              <div>
                <div id="CalcLabel"></div>
                <div id="CalcResult"></div>
              </div>
            </div>
          </Box>
        )
      }

  }


export default SimpleInterestCalc;
