import React, { Component } from 'react';
import Finance from 'financejs';

class GoalSeek extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      account: props.account,
      targetValue: props.targetValue
    };
  }
  seek() {
    const finance = new Finance();
    const n = account.length;  // number of periods
    let rate = .001;  // initial guess for the rate of return

    // calculate the net present value of the investments at the initial guess for the rate of return
    let npv = 0;
    for (let i = 0; i < n; i++) {
      npv = finance.PV(rate, account[i].balEOY);
    }

    // iterate until the net present value is close enough to the target value
    const tolerance = .01;  // tolerance for the error between the net present value and the target value
    while (Math.abs(npv - targetValue) > tolerance) {
      // adjust the rate of return based on the error between the net present value and the target value
      var diff = Math.abs(npv - targetValue);
      rate += (targetValue - npv) / npv;

      // recalculate the net present value at the new rate of return
      //npv = account[0].balBOY;
      for (let i = 0; i < n; i++) {
        npv = finance.PV(rate, account[i].balEOY);

      }
      return rate;
    }
  }
  npv(accountValuesArr, rate) {
    let npv = 0;
    for (let i = 0; i < accountValuesArr.length; i++) {
      npv += accountValuesArr[i] / Math.pow(1 + rate, i);
    }
    return npv;
  }
}

export default GoalSeek;
