import React, { Component } from 'react';
import Finance from 'financejs';

class GoalSeek extends React.Component {
  constructor() {
    //
    super();
    this.finance = new Finance();
  }
  seek(initialValue, dataTable, targetValue) {
    const n = dataTable.length;  // number of periods
    let rate = .1;  // initial guess for the rate of return

    // calculate the net present value of the investments at the initial guess for the rate of return
    //let pvir = this.calcpval(rate, dataTable[n - 1].balEOY, 1);
    let pvir = this.calcpvir(rate, dataTable[n - 1].annPay, n, initialValue);

    console.log("initialValue:", initialValue, " pvir:", pvir, " targetValue:", targetValue);

    // iterate until the net present value is close enough to the target value, or until the maximum number of iterations is reached
    const tolerance = 1e-2;  // tolerance for the error between the net present value and the target value
    const maxIterations = 1e+6;  // maximum number of iterations
    let iterations = 0;  // current number of iterations
    while ((Math.abs((pvir - targetValue) / pvir)) * 100 < tolerance && iterations < maxIterations) {
      // adjust the rate of return based on the error between the net present value and the target value
      rate = this.guess(1, -1);

      // recalculate the net present value at the new rate of return
      pvir = this.calcpvir(rate, dataTable[n - 1].annPay, n, initialValue);
      //pvir = this.calcpval(rate, dataTable[n - 1].balEOY, 1);

      iterations++;
    }

    if (iterations === maxIterations) {
      console.log(iterations, Math.abs((pvir - targetValue) / pvir), pvir, rate);

      throw new Error('Could not find a solution within the maximum number of iterations');
    }
    console.log(iterations, Math.abs((pvir - targetValue) / pvir), pvir, rate);

    return rate;

  }

  calcpval(r, data, n) {
    let val = this.finance.PV(r, data, n);
    return val;
  }

  calcavg(dataTable) {
    let avg = 0;
    let n = dataTable.length;
    for (let i = 0; i < n; i++) {
      avg += dataTable[i].intRate;
    }
    avg = avg / n;
    return avg;
  }

  calcpvir(rate, annPay, numYears, initialValue) {
    let pvir = Math.round((1 + rate) * annPay * (((1 + rate) ** (numYears - 1)) / rate) + initialValue * ((1 + rate / 1) ** numYears));
    return pvir;
  }

  // generate a random number within a range
  guess = function (hi, lo) {
    var guess = Math.random() * (hi - lo) + lo;
    return guess;
  }
}

export default GoalSeek;
