import './RORCalc.css';
import ROR from './helpers/ROR.js';
import GS from './helpers/GoalSeek.js';
import Finance from 'financejs';
import ToolTip from './snippets/tooltip.js';
import React from 'react';
import { Component } from 'react';
import { withStyles } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import ResultsWindow from './templates/resultsWindow';

var _this;

class RORCalc extends Component {
      constructor() {
        super();
        this.def = {  presVal: 1000,
                      annPay: 1000,
                      mgmtFee: 1,
                      yrStart: 1963,
                      numYears: 5,
                      stocks: 100,
                      bonds: 0,
                      treasuries: 0
                    };
        this.ROR = ROR;
        this.GS = GS;
        _this = this;
      }

      changed(event) {
        // if the values in the fields change, do stuff
      }

      format(event, ID) {
        var val;
        switch (ID) {
          case "presVal" :
            val = _this.def.presVal;
            break;
          case "annPay" :
            val = _this.def.interestRate;
            break;
          case "mgmtFee" :
            val = _this.def.annualContributions;
            break;
          case "yrStart" :
            val = _this.def.numYears;
            break;
          case "numYears" :
            val = _this.def.numYears;
            break;
          case "stocks" :
            val = _this.def.numYears;
            break;
          case "bonds" :
            val = _this.def.numYears;
            break;
          case "treasuries" :
            val = _this.def.numYears;
            break;
        }

        console.log("The value of " + ID + " is: " + val);
        document.getElementById(ID).value = val.toLocaleString();
      }

      calculate() {
        // RORCalculator Functions
        document.getElementById('CalcResult').classList.toggle('hidden');
      }

      render() {
        return (
            <Box
              sx={{ flexGrow: 1, bgcolor: 'background.paper', display: 'flex'}}
            >
            <div className="container">
              <div id="CalcDataEntry">
                <table className="ror-data">
                <tbody>
                  <tr>
                    <td>
                      <label>Present Value <ToolTip /></label>
                      <input  type="text"
                              id="presVal"
                              placeholder={this.def.presVal.toLocaleString()}
                              onChange={(event)=>this.changed(event)}
                              onBlur={(event)=>this.format(event, 'presVal')}
                              />
                    </td>
                    <td>
                      <label>Annual Payment<ToolTip /></label>
                      <input  type="text"
                              id="annPay"
                              placeholder={this.def.annPay.toLocaleString()}
                              onChange={(event)=>this.changed(event)}
                              onBlur={(event)=>this.format(event, 'annPay')}
                              />
                    </td>
                    <td>
                      <label>Management Fee<ToolTip /></label>
                      <input  type="text"
                              id="mgmtFee"
                              placeholder={this.def.mgmtFee.toLocaleString()}
                              onChange={(event)=>this.changed(event)}
                              onBlur={(event)=>this.format(event, 'mgmtFee')}
                              />
                    </td>
                    <td>
                      <label>Starting Year<ToolTip /></label>
                      <input  type="text"
                              id="yrStart"
                              placeholder={this.def.yrStart.toLocaleString()}
                              onChange={(event)=>this.changed(event)}
                              onBlur={(event)=>this.format(event, 'yrStart')}
                              />
                    </td>
                    <td>
                      <label>Number of Years<ToolTip /></label>
                      <input  type="text"
                              id="numYears"
                              placeholder={this.def.numYears.toLocaleString()}
                              onChange={(event)=>this.changed(event)}
                              onBlur={(event)=>this.format(event, 'numYears')}
                              />
                    </td>
                  </tr>

                  <tr>
                    <td><h5 class="calc-heading">Portfolio Allocation</h5></td>
                    <td>
                      <label>Stocks <ToolTip /></label>
                      <input  type="text"
                              id="stocks"
                              placeholder={this.def.stocks.toLocaleString()}
                              onChange={(event)=>this.changed(event)}
                              onBlur={(event)=>this.format(event, 'stocks')}
                              />
                    </td>
                    <td>
                    <label>Bonds <ToolTip /></label>
                      <input  type="text"
                              id="bonds"
                              placeholder={this.def.bonds.toLocaleString()}
                              onChange={(event)=>this.changed(event)}
                              onBlur={(event)=>this.format(event, 'bonds')}
                              />
                    </td>
                    <td>
                    <label>Treasuries <ToolTip /></label>
                      <input  type="text"
                              id="treasuries"
                              placeholder={this.def.treasuries.toLocaleString()}
                              onChange={(event)=>this.changed(event)}
                              onBlur={(event)=>this.format(event, 'treasuries')}
                              />
                    </td>
                  </tr>

                  </tbody>
                </table>
                <Button className="calc-btn"
                        variant="contained"
                        size="large"
                        onClick={this.calculate}
                >
                  Calculate
                </Button>
              </div>
              <div>
                <div id="CalcLabel"></div>
                <div id="CalcResult hidden">
                  <ResultsWindow />
                </div>
              </div>
            </div>
          </Box>
        )
      }

  }


export default RORCalc;
