import './RORCalc.css';
import RATE from './helpers/rate.js';
import GOAL from './helpers/goalseek.js';
import Finance from 'financejs';
import ToolTip from './snippets/tooltip.js';
import React, { Component } from 'react';
import { useEffect } from 'react';
import { withStyles } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';

const finance = new Finance();
var _this;

class RORCalc extends Component {
  constructor() {
    super();
    this.def = {
      BOY:            1000,
      annPay:         1000,
      mngFee:         1.00,
      startYear:      1963,
      numYears:       5,
      percentStocks:  100,
      percentBonds:   0,
      percentTreas:   0,
      api:        "http://18.188.124.75:5000/all",
      econData:       null,
      items:          null,
      histData:   [],
      dataTable: [],
    };

    this.RATE = new RATE();
    this.GOAL = GOAL;
    _this = this; //remember to use _this to reference the def variables
  }

  componentDidMount() {
      fetch(this.def.api)
        .then( result => {
            return result.json()
          })
        .then( data => {
            this.def.histData = data; // put the data into global histData array
            console.log("data contents ==> ", this.def.histData);
            var yearIndex;
            for(var i = 0; i <= this.def.histData.length; i++) {
              if (this.def.histData[i].Year == this.def.startYear) {
                yearIndex = i;
                console.log("Starting Year: ", this.def.startYear, "Index: ", yearIndex);
                break;
              }
            }

            var balEOY = 0;
            var hData = this.def.histData,
                balBOY = this.def.BOY,
                annPay = this.def.annPay;

            for (var j = yearIndex; j <= (yearIndex + this.def.numYears); j++) {
              var rate = hData[j].SandP,
                  year = hData[j].Year,
                  intRate = +(hData[j].SandP).toFixed(2),
                  mngFee = Math.round((this.def.mngFee/100)*(balBOY + annPay));

              var fv = Math.round((finance.FV(rate, this.def.annPay + balBOY, 1)) - mngFee);

              if ( j > yearIndex ) {
                balBOY = balEOY;
                fv = Math.round((finance.FV(rate, this.def.annPay + balBOY, 1)) - mngFee);
                balEOY = fv;
              } else{
                balEOY = fv;
              };

              this.def.dataTable.push({
                index: j,
                year: year,
                balBOY: balBOY,
                intRate: intRate,
                annPay: annPay,
                mngFee: mngFee,
                balEOY: balEOY,
              });
              this.renderTable(year, balBOY, intRate, annPay, mngFee, balEOY);
            }
            //RATE (periods, payment, present, future, type, guess)
            var rate = this.RATE(this.def.numYears, this.def.annPay, this.def.dataTable[0].balBOY, balEOY, 1, 1);
            console.log("RATE=",rate);
            console.log(this.def.dataTable);
          }
        ).catch( (error) => {
            console.log(error)
        });
  }


    renderTable(year, balBOY, intRate, annPay, mngFee, balEOY) {
      //render the table of values for each year
      // Years from Start	| Year	| Balance BOY	| Interest Rate	| Annual Payment	| Management Fee	| Balance EOY
      console.log(year, balBOY, intRate, annPay, mngFee, balEOY);
      let table = document.getElementById('CalcTable');
      let tableRow = `<div class='row'><div class='year'>${year}</div><div class='balBOY'>${balBOY}</div><div class='intRate'>${intRate}</div><div class='annPay'>${annPay}</div><div class='mngFee'>${mngFee}</div><div class='balEOY'>${balEOY}</div></div>`;
      table.innerHTML += tableRow;
    }


  changed(event) {
    _this.def.BOY = (document.getElementById('BOY').value == "") ? _this.def.BOY : +(document.getElementById('BOY').value);
    _this.def.futureVal = (document.getElementById('futureVal').value == "") ? _this.def.futureVal : Number(document.getElementById('futureVal').value);
    _this.def.annualContributions = (document.getElementById('annualContributions').value == "") ? _this.def.annualContributions : Number(document.getElementById('annualContributions').value);
    _this.def.numYears = (document.getElementById('numYears').value == "") ? _this.def.numYears : Number(document.getElementById('numYears').value);
  }


  calculate() {
    // * @usage RATE($periods, $payment, $present, $future, $type, $guess)
    var per = _this.def.numYears,
        cont = _this.def.annualContributions * -1,
        pv = _this.def.presVal,
        fv = _this.def.futureVal;

    const rate = _this.RATE(per, cont, pv, fv, 0, 1) * 100;

    var calcResult = document.getElementById("CalcResult");
    var calcLabel = document.getElementById("CalcLabel");
    calcLabel.innerHTML = "The Interest Rate is"
    calcResult.innerHTML = rate + "%";
  }


  render() {
    return (
      <Box
        sx={{ flexGrow: 1, bgcolor: 'background.paper', display: 'flex' }}
      >
        <div className="container">
          <div id="RORCalcWrapper">
            <div className="ror-data columns">
                <div className="col1">
                  <div id="input1">
                    <label>Present Value <ToolTip /></label>
                    <input type="number"
                      id="BOY"
                      placeholder={this.def.BOY}
                      onChange={(event) => this.changed(event)} />
                  </div>
                  <div id="input2">
                    <label>Annual Payment <ToolTip /></label>
                    <input type="number"
                      id="annPay"
                      placeholder={this.def.annPay}
                      onChange={(event) => this.changed(event)} />
                  </div>
                  <div id="input3">
                    <label>Management Fee <ToolTip /></label>
                    <input type="number"
                      id="mngFee"
                      placeholder={this.def.mngFee}
                      onChange={(event) => this.changed(event)} />
                  </div>
                  <div id="input4">
                    <label>Starting Year <ToolTip /></label>
                    <input type="number"
                      id="startYear"
                      placeholder={this.def.startYear}
                      onChange={(event) => this.changed(event)} />
                  </div>
                  <div id="input5">
                    <label>Number of Years <ToolTip /></label>
                    <input type="number"
                      id="numYears"
                      placeholder={this.def.numYears}
                      onChange={(event) => this.changed(event)} />
                  </div>
                </div>
                <div className="col2">
                  <div className="group">
                    <h5>PORTFOLIO ALLOCATION</h5>
                    <div>
                      <label>Stock Market (S&amp;P 500) <ToolTip /></label>
                      <input type="number"
                        id="stocksPortfolio"
                        placeholder={this.def.percentStocks}
                        onChange={(event) => this.changed(event)} />
                    </div>
                    <div>
                      <label>Corporate Bonds <ToolTip /></label>
                      <input type="number"
                        id="bondsPortfolio"
                        placeholder={this.def.percentBonds}
                        onChange={(event) => this.changed(event)} />
                    </div>
                    <div>
                      <label>Government Treasuries  <ToolTip /></label>
                      <input type="number"
                        id="treasPortfolio"
                        placeholder={this.def.percentTreas}
                        onChange={(event) => this.changed(event)} />
                    </div>
                  </div>
                </div>
            </div>
            <Button className="calc-btn"
              variant="contained"
              size="large"
              onClick={this.calculate}
            >
              Calculate
            </Button>
          </div>
          <div>
            <div id="CalcTable"></div>
            <div id="CalcLabel"></div>
            <div id="CalcResult"></div>
          </div>
        </div>
      </Box>
    )
  }

}


export default RORCalc;
